create database dbparcial;
use dbparcial;

create table proveedor(
	proveedor_id integer auto_increment,
    codigo varchar(10) not null,
    nombre varchar(200) not null,
    PRIMARY KEY(proveedor_id)
);

create table producto(
	producto_id integer auto_increment,
    codigo varchar(100) not null,
    descripcion varchar(2000) not null,
    activo numeric(1),
    PRIMARY KEY(producto_id)
);

create table orden_compra(
	orden_compra_id integer auto_increment,
    numero_orden varchar(20) not null,
    pedido_el timestamp not null,
    proveedor_id integer,
    PRIMARY KEY(orden_compra_id),
    FOREIGN key(proveedor_id)references proveedor(proveedor_id)
);

create table orden_compra_detalle(
	orden_compra_detalle_id integer auto_increment,
    producto_id integer not null,
    orden_compra_id integer not null,
    cantidad integer not null,
    precio_compra numeric(16,6) not null,
    observaciones varchar(2000), 
    PRIMARY KEY(orden_compra_detalle_id),
    FOREIGN KEY(producto_id)references producto(producto_id),
    FOREIGN KEY(orden_compra_id)references orden_compra(orden_compra_id)
);







